<script>

    export default {

        props: {
            node: {type: Object, required: true}
        },

        data() {
            return {};
        },

        computed: {

            fileGroup() {
                const {extension} = this.node;
                const {extensionMap} = this.$config;

                // Find the group where this nodes extension correpondends to
                for (const [group, extensions] of Object.entries(extensionMap)) {
                    if (extensions.includes(extension)) {
                        return group;
                    }
                }

                return null;
            }
        }
    };

</script>

<style lang="scss" scoped>

    .file-type-preview {
        @include flex(column);

        svg {
            @include size(3.5em);
            margin: 1.5em auto;
            align-self: flex-start;
            fill: $palette-asphalt;
        }

        > button {
            @include font(600, 0.95em);
            background: $palette-asphalt;
            color: $palette-snow-white;
            transition: all 0.3s;
            padding: 0.4em 0.8em;
            border-radius: 0.15em;
            width: 100%;

            &:hover {
                background: lighten($palette-asphalt, 5);
            }
        }
    }

</style>
